mapper.select:
select match_state
from goods
where Goods_Num = #{Goods_Num}

mapper.update:
		update goods set
		Match_State = '배송중'
		where goods_Num = #{goods_Num}

update goods set
Match_State = '배송완료'
where goods_Num = #{goods_Num}

update delivery set
departure_Time = sysdate
where goods_Num = #{goods_Num}

update delivery set
arrival_Time = sysdate
where goods_Num = #{goods_Num}
