mapper.select:
select *
from member
where mem_id=#{id} and mem_pw=#{pass}

mapper.update:
update member set mem_name=#{member.mem_name}, mem_birth=#{member.mem_birth}, mem_zipcode=#{member.mem_zipcode}, mem_addr=#{member.mem_addr}, mem_addr_detail=#{member.mem_addr_detail}, mem_gender=#{member.mem_gender}, mem_phone=#{member.mem_phone}, mem_email=#{member.mem_email},
mem_img=#{member.mem_img},
mem_account=#{member.mem_account} where mem_id=#{member.mem_id}

update member
set
mem_pw=#{temppass}
where mem_id=#{id} and mem_email=#{email}

update member
set
point_total=#{pointTotal}
where mem_id=#{memId}
