mapper.select:
select *
from member
where mem_id=#{id} and mem_pw=#{pass}

mapper.update:
update member
set
mem_pw=#{temppass}
where mem_id=#{id} and mem_email=#{email}

update member
set
mem_pw=#{newpass}
where mem_id=#{id}

update member
set
point_total=#{pointTotal}
where mem_id=#{memId}
